import React, { useEffect, useState } from 'react';
import { fetchTasks, updateTask, deleteTask } from '../utils/taskUtils';
import { toast } from 'react-toastify';
import TaskColumn from './TaskColumn';
import TaskForm from './TaskForm';

function TaskList() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showNewTaskForm, setShowNewTaskForm] = useState(false);

  useEffect(() => {
    loadTasks();
  }, []);

  const loadTasks = async () => {
    try {
      setLoading(true);
      const fetchedTasks = await fetchTasks();
      setTasks(fetchedTasks);
    } catch (err) {
      toast.error('Failed to load tasks');
    } finally {
      setLoading(false);
    }
  };

  const handleTaskUpdated = async (taskId, updates) => {
    try {
      await updateTask(taskId, updates);
      loadTasks();
    } catch (error) {
      toast.error('Failed to update task');
    }
  };

  const handleTaskDeleted = async (taskId) => {
    try {
      await deleteTask(taskId);
      loadTasks();
    } catch (error) {
      toast.error('Failed to delete task');
    }
  };

  const handleNewTaskAdded = () => {
    loadTasks();
    setShowNewTaskForm(false);
  };

  if (loading) {
    return <div>Loading tasks...</div>;
  }

  const todoTasks = tasks.filter(task => !task.completed && !task.inProgress);
  const inProgressTasks = tasks.filter(task => !task.completed && task.inProgress);
  const doneTasks = tasks.filter(task => task.completed);

  return (
    <div className="task-board">
      <h1>Task Board</h1>
      <button 
        className="new-task-button main" 
        onClick={() => setShowNewTaskForm(true)}
      >
        Create New Task
      </button>
      {showNewTaskForm && (
        <TaskForm 
          onTaskAdded={handleNewTaskAdded} 
          onCancel={() => setShowNewTaskForm(false)}
        />
      )}
      <div className="columns-container">
        <TaskColumn 
          title="To Do" 
          tasks={todoTasks} 
          onTaskUpdated={handleTaskUpdated}
          onTaskDeleted={handleTaskDeleted}
        />
        <TaskColumn 
          title="In Progress" 
          tasks={inProgressTasks} 
          onTaskUpdated={handleTaskUpdated}
          onTaskDeleted={handleTaskDeleted}
        />
        <TaskColumn 
          title="Done" 
          tasks={doneTasks} 
          onTaskUpdated={handleTaskUpdated}
          onTaskDeleted={handleTaskDeleted}
        />
      </div>
    </div>
  );
}

export default TaskList;
