import React from 'react';
import { deleteTask } from '../utils/taskUtils';
import { toast } from 'react-toastify';

function TaskItem({ task, onTaskUpdated }) {
  const handleDeleteTask = async () => {
    try {
      await deleteTask(task._id);
      onTaskUpdated(task._id, { deleted: true });
    } catch (error) {
      toast.error('Failed to delete task');
    }
  };

  const handleMoveTask = (status) => {
    onTaskUpdated(task._id, status);
  };

  return (
    <div className="task-item">
      <div className="task-content">
        <span>{task.description}</span>
      </div>
      <div className="task-actions">
        {!task.inProgress && !task.completed && (
          <button onClick={() => handleMoveTask({ inProgress: true })}>Start</button>
        )}
        {task.inProgress && (
          <button onClick={() => handleMoveTask({ completed: true, inProgress: false })}>Complete</button>
        )}
        {task.completed && (
          <button onClick={() => handleMoveTask({ completed: false, inProgress: false })}>Reopen</button>
        )}
        <button onClick={handleDeleteTask}>Delete</button>
      </div>
    </div>
  );
}

export default TaskItem;
