import axios from './axios';

const getAuthHeader = () => {
  const token = localStorage.getItem('token');
  return { headers: { Authorization: `Bearer ${token}` } };
};

export const fetchTasks = async () => {
  try {
    const response = await axios.get('/api/tasks', getAuthHeader());
    return response.data;
  } catch (error) {
    console.error('Error fetching tasks:', error);
    throw error;
  }
};

export const createTask = async (taskData) => {
  try {
    const response = await axios.post('/api/tasks', taskData, getAuthHeader());
    return response.data;
  } catch (error) {
    console.error('Error creating task:', error);
    throw error;
  }
};

export const updateTask = async (taskId, taskData) => {
  try {
    const response = await axios.put(`/api/tasks/${taskId}`, taskData, getAuthHeader());
    return response.data;
  } catch (error) {
    console.error('Error updating task:', error);
    throw error;
  }
};

export const deleteTask = async (taskId) => {
  try {
    await axios.delete(`/api/tasks/${taskId}`, getAuthHeader());
  } catch (error) {
    console.error('Error deleting task:', error);
    throw error;
  }
};
